mapper.insert:

  SELECT COALESCE(MAX(CODE_NO), 0)+1 AS CODE_NO FROM TB_CODE

INSERT INTO TB_CODE (
  CODE_NO,
  USER_NO,
  CODE_ID,
  CODE_DESCRIPTION,
  CODE_CONTENT,
  CODE_CREATED_DATE,
  CODE_CREATED_TIME,
  CODE_USE_YN
) VALUES (
  #{codeVo.codeNo}
  ,#{codeVo.userNo}
  ,#{codeVo.codeId}
  ,#{codeVo.codeDescription}
  ,#{codeVo.codeContent}
  ,CURRENT_DATE
  ,CURRENT_TIME
  ,#{codeVo.codeUseYn}
)

mapper.delete:
DELETE FROM TB_CODE
WHERE 1=1
AND CODE_NO = #{codeVo.codeNo}
